function getTime(input) {
    try {
		let isnum = /^\d+$/.test(input);
		let hour = "";
		let min = "00";

		if(isnum){
			let value = parseInt(input);
			if(value > 23 || value < 0){
				return "false";
			}
			if (value < 10){
				hour = "0" + value;
			}
			else{
				hour = "" + value;
			}
		}
		else{
			if(input == "meiodia" || input == "meidia" || input == "mei dia" || input == "meio dia"){
				hour = "12";
			}
			else if (input == "meia noite" || input == "meianoite"){
				hour = "00";
			}
			else{
				input = input.replace("horas", "h");
				input = input.replace("hora", "h");
				input = input.replace("hras", "h");
				input = input.replace("hra", "h");
				input = input.replace("hrs", "h");
				input = input.replace("hr", "h");
				input = input.replace("h", ":");
				input = input.replace("minutos", "m");
				input = input.replace("minuto", "m");
				input = input.replace("mins", "m");
				input = input.replace("min", "m");
				input = input.replace("m", "");
				input = input.replace(/\s/g, "");
				input = input.replace(".", ":");

				hour = input.split(":")[0];
				min = input.split(":")[1];
			}

			if (parseInt(hour) > 23 || parseInt(min) > 59){
				return false;
			}

			if (parseInt(hour) < 10){
				hour = "0" + parseInt(hour);
			}

			if (parseInt(min) < 10){
				min = "0" + parseInt(min);
			}

			if (min == ""){
				min = "00";
			}

			if ( ! /^\d+$/.test(hour) || ! /^\d+$/.test(min) || hour.length > 2 || min.length > 2){
				return false;
			}

		}

		return hour + ":" + min;

    } catch (ex) {
        return "false";
    }
}

